INSERT INTO SPEAKER(FIRST_NAME, LAST_NAME, TWITTER, GITHUB, BIO) values ('Mark', 'Paluch', 'mp911de', 'mp911de', 'Mark is Software Craftsman, Spring Data Engineer at Pivotal, a member of the CDI 2.0 expert group, and Lead of the lettuce Redis driver. He has been into developing Java server-, frontend-, and web applications for over 12 years and his focus is now on software architecture, Spring and Redis clients.');
